function wpbootstrap_scripts_with_jquery()
{
	// Alterando o registro
	 wp_deregister_script( 'jquery' );

    wp_register_script( 'jquery', get_template_directory_uri() . '/js/jquery-1.11.3.min.js');
	wp_register_script( 'bootstrap', get_template_directory_uri() . '/bootstrap/js/bootstrap.js', array( 'jquery' ));
	wp_register_script( 'cycle', get_template_directory_uri() . '/js/cycle.js', array( 'jquery' ));
	wp_register_script( 'pace', get_template_directory_uri() . '/js/pace.min.js', array( 'jquery' ));

	wp_enqueue_script( 'jquery' ); 		// Jquery padrão
	wp_enqueue_script( 'cycle' ); 		// Efeitos de transição
	wp_enqueue_script( 'pace' );  		// Barra de progresso no topo da página
	wp_enqueue_script( 'bootstrap' ); 	// Padrão do tema
}

add_action( 'wp_enqueue_scripts', 'wpbootstrap_scripts_with_jquery' );

