function add_custom_meta_box()
{
	add_meta_box("meta-box-chapeu", "Chapéu", "custom_meta_box_chapeu", "post", "side", "high", null);
	
	add_meta_box("meta-box-data_evento", "Evento", "custom_meta_box_evento", "post", "side", "high", null);
}

add_action("add_meta_boxes", "add_custom_meta_box");
/* fim do metabox */

/* salvando os dados do metabox */
function save_custom_meta_box($post_id, $post, $update)
{
    if (!isset($_POST["meta-box-nonce"]) || !wp_verify_nonce($_POST["meta-box-nonce"], basename(__FILE__)))
        return $post_id;

    if(!current_user_can("edit_post", $post_id))
        return $post_id;

    if(defined("DOING_AUTOSAVE") && DOING_AUTOSAVE)
        return $post_id;

    $slug = "post";
    if($slug != $post->post_type)
        return $post_id;

    $meta_box_chapeu_value = "";

    if(isset($_POST["_chapeu"]))
    {
        $meta_box_chapeu_value = $_POST["_chapeu"];
    }
	update_post_meta($post_id, "_chapeu", $meta_box_chapeu_value);
	
	$meta_box_dataevento_value = "";

    if(isset($_POST["_data_evento"]))
    {
        $meta_box_dataevento_value = $_POST["_data_evento"];
    }
    update_post_meta($post_id, "_data_evento", $meta_box_dataevento_value);
}

add_action("save_post", "save_custom_meta_box", 10, 3);
/* fim do salvamento */

