/* Função para notícias relacionadas usando Categorias */

function noticias_relacionadas() {

$orig_post = $post;
	global $post;
	$categories = get_the_category($post->ID);
	
	if ($categories) {
		$category_ids = array();
		foreach($categories as $individual_category) $category_ids[] = $individual_category->term_id;

		$args=array(
		'category__in' => $category_ids,
		'post__not_in' => array($post->ID),
		'posts_per_page'=> 3, // posts a mostrar
		'caller_get_posts'=>1
		);

		$my_query = new wp_query( $args );
		if( $my_query->have_posts() ) {
			echo '<div id="related_post"><h3 class="related_title">NOTÍCIAS <B>RELACIONADAS</B></h3><ul>';
			while( $my_query->have_posts() ) {
				$my_query->the_post(); 

				?>

				<li class="related_item">
					<div class="related_thumb" style="display: none;">
						<a href="<?php the_permalink()?>" rel="bookmark" title="<?php the_title(); ?>"><?php // the_post_thumbnail(); ?></a>
					</div>

					<div class="related_content">
						<p><span class="related_date"><?php the_time('j M. Y') ?></span><br>
							<a href="<?php the_permalink()?>" rel="bookmark" title="<?php the_title(); ?>"><?php the_title(); ?></a>
						</p>

					</div>
				</li>

				<?php
			}
		}
	}
	echo '</ul></div>';
	$post = $orig_post;
	wp_reset_query(); 
}
			
/* fim da função notícias relacionadas */


